import React, { useState, useEffect } from 'react';
import axios from 'axios';

function DesignationForm() {
  const [title, setTitle] = useState('');
  const [designations, setDesignations] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    axios.get('/designations')
      .then(response => {
        setDesignations(response.data);
      })
      .catch(error => {
        console.error('Error fetching designations: ', error);
      });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (title.trim() === '') return;

    axios.post('/designations', { title })
      .then(response => {
        setTitle('');
        fetchData();
      })
      .catch(error => {
        console.error('Error adding designation: ', error);
      });
  };

  const handleDelete = (code) => {
    axios.delete(`/designations/${code}`)
      .then(response => {
        fetchData();
      })
      .catch(error => {
        console.error('Error deleting designation: ', error);
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Enter title"
          required
        />
        <button type="submit">Add Designation</button>
      </form>

      <ul>
        {designations.map(designation => (
          <li key={designation.code}>
            {designation.title}
            <button onClick={() => handleDelete(designation.code)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DesignationForm;
