import { Link } from 'react-router-dom';
import { useAuth } from './security/AuthContext';

function HeaderComponent() {
    const authContext = useAuth();
    const isAuthenticated = authContext.isAuthenticated;

    function logout() {
        authContext.logout();
    }

    return (
        <header className="border-bottom border-light border-10 mb-10 p-2">
            <div className="container">
                <div className="row">
                    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                        {isAuthenticated && 
                            <Link className="navbar-brand ms-2 fs-2 fw-bold text-white" to="/welcome">Saturn&Sons</Link>
                        }
                        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse" id="navbarNav">
                            <ul className="navbar-nav me-auto">
                                {isAuthenticated && (
                                    <>
                                        <li className="nav-item">
                                            <Link className="nav-link" to="/welcome">Home</Link>
                                        </li>
                                        <li className="nav-item">
                                            <Link className="nav-link" to="/designations">Designations</Link>
                                        </li>
                                        <li className="nav-item">
                                            <Link className="nav-link" to="/employees">Employees</Link>
                                        </li>
                                    </>
                                )}
                            </ul>
                            <ul className="navbar-nav">
                                {!isAuthenticated && (
                                    <li className="nav-item">
                                        <Link className="nav-link" to="/login">Login</Link>
                                    </li>
                                )}
                                {isAuthenticated && (
                                    <li className="nav-item">
                                        <Link className="nav-link" to="/logout" onClick={logout}>Logout</Link>
                                    </li>
                                )}
                            </ul>
                        </div>
                    </nav>
                </div>
            </div>
        </header>
    );
}

export default HeaderComponent;
