import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FormData() {
  const [title, setTitle] = useState('');
  const [data, setData] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    axios.get('http://localhost:8080/designations')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data: ', error);
      });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (title.trim() === '') return;
    
    if (isEditing) {
      axios.put(`http://localhost:8080/}`, { title })
        .then(response => {
          fetchData();
          setTitle('');
          setIsEditing(false);
          setEditId(null);
        })
        .catch(error => {
          console.error('Error updating data: ', error);
        });
    } else {
      axios.post('http://localhost:8080/api/data', { title })
        .then(response => {
          fetchData();
          setTitle('');
        })
        .catch(error => {
          console.error('Error adding data: ', error);
        });
    }
  };

  const handleDelete = (id) => {
    axios.delete(`http://localhost:8080/api/data/${id}`)
      .then(response => {
        fetchData();
      })
      .catch(error => {
        console.error('Error deleting data: ', error);
      });
  };

  const handleEdit = (id, title) => {
    setTitle(title);
    setIsEditing(true);
    setEditId(id);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Enter title"
          required
        />
        <button type="submit">{isEditing ? 'Update' : 'Add'}</button>
      </form>
      
      <ul>
        {data.map(item => (
          <li key={item.id}>
            {item.title}
            <button onClick={() => handleEdit(item.id, item.title)}>Edit</button>
            <button onClick={() => handleDelete(item.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default FormData;
