import { useEffect, useState } from 'react'
import {useParams, useNavigate} from 'react-router-dom'
import { retrieveDesignationByCodeApi, updateDesignationApi, createDesignationApi } from './api/DesignationApiService'
import { useAuth } from './security/AuthContext'
import {Formik, Form, Field, ErrorMessage} from 'formik'
// import moment from 'moment'

export default function DesignationComponet() {
    
    const {code} = useParams()
    
    const[title, setTitle] = useState('')

    const authContext = useAuth()
    const navigate = useNavigate()
    
    const username = authContext.username
    
    useEffect(
        () => retrieveDesignations(),
        [code]
        )

    function retrieveDesignations(){
        if(code != -1) {
            retrieveDesignationByCodeApi(code)
            .then(response => {
                setTitle(response.data.title)
            })
            .catch(error => console.log(error))
        }
    }

    function onSubmit(values) {
        console.log(values)
        
        const designation = {
            code: values.code,
            title: values.title,
        }

        console.log(designation)

        if(code==-1) {
            createDesignationApi(designation)
            .then(response => {
                navigate('/designations')
            })
            .catch(error => console.log(error))
    
        } else {
            updateDesignationApi(designation)
            .then(response => {
                navigate('/designations')
            })
            .catch(response => <h1 className = "alert alert-warning"> {response.data} </h1>)
            if(response.status!=201)
                {
                    <div className = "alert alert-warning"> {response.data} </div>
                }
        }
    }

    // function validate(values) {
    //     let errors = {
    //         // description: 'Enter a valid description',
    //         // targetDate: 'Enter a valid target date'
    //     }

    //     if(values.description.length<5) {
    //         errors.description = 'Enter atleast 5 characters'
    //     }

    //     if(values.targetDate == null || values.targetDate=='' || !moment(values.targetDate).isValid()) {
    //         errors.targetDate = 'Enter a target date'
    //     }

    //     console.log(values)
    //     return errors
    // }

    return (
        <div className="container">
            <h1>Enter Designation Details </h1>
            <div>
                <Formik initialValues={ { title } } 
                    enableReinitialize = {true}
                    onSubmit = {onSubmit}
                    // validate = {validate}
                    // validateOnChange = {false}
                    // validateOnBlur = {false}
                >
                {
                    (props) => (
                        <Form>
                            {/* <ErrorMessage 
                                name="description"
                                component="div"
                                className = "alert alert-warning"
                            />
                            
                            <ErrorMessage 
                                name="targetDate"
                                component="div"
                                className = "alert alert-warning"
                            /> */}

                            <fieldset className="form-group">
                                <label>Title</label>
                                <Field type="text" className="form-control" name="title" />
                            </fieldset>
                            <div>
                                <button className="btn btn-success m-5" type="submit">Save</button>
                            </div>
                        </Form>
                    )
                }
                </Formik>
            </div>

        </div>
    )
}
